 26|) {
 27|
 28| private var currentEventId: Int? = null
 29| private var currentUserId: Int? = null
 30|
 31| override fun onEvent(event: EventDetailsEvent) {
 32| when (event) {
 33| is EventDetailsEvent.LoadEventDetails -> loadEventDetails(event.eventId)
 34| is EventDetailsEvent.RegisterClicked -> registerForEvent()
 35| is EventDetailsEvent.CancelRegistrationClicked -> cancelRegistration()
 36| is EventDetailsEvent.BackClicked -> emitSideEffect(EventDetailsSideEffect.NavigateBack)
 37| }
 38| }
 39|
 40| private fun loadEventDetails(eventId: Int) {
 41| currentEventId = eventId
 42| updateState { EventDetailsState.IsLoading(true) }
 43|
 44| viewModelScope.launch {
 45| // Get userId from DataStore
 46| val userId = dataStoreManager.getPreference(PreferenceKeys.USER_ID, "0").first().toIntOrNull()
 47| currentUserId = userId
 48|
 49| getEventDetailsUseCase(eventId).collect { resource ->
 50| when (resource) {
 51| is Resource.Loader -> {
 52| updateState { EventDetailsState.IsLoading(resource.isLoading) }
 53| }
 54| is Resource.Success -> {
 55| updateState {
 56| EventDetailsState.Success(
 57| eventDetails = resource.data,
 58| registrationId = null, // Will be populated if user is registered
 59| isRegistering = false
 60| )
 61| }
 62| }
 63| is Resource.Error -> {
 64| updateState { EventDetailsState.Error(EventDetailsError.UnknownError) }
 65| emitSideEffect(EventDetailsSideEffect.ShowError(EventDetailsError.UnknownError))
 66| }
 67| }
 68| }
 69| }
 70| }
 71|
 72| private fun registerForEvent() {
 73| val eventId = currentEventId ?: return
 74| val userId = currentUserId ?: run {
 75| emitSideEffect(EventDetailsSideEffect.ShowToast("User not logged in"))
 76| return
 77| }
 78|
 79| val currentState = state.value
 80| if (currentState !is EventDetailsState.Success) return
 81|
 82| // Update UI to show registering state
 83| updateState {
 84| (it as EventDetailsState.Success).copy(isRegistering = true)
 85| }
 86|
 87| viewModelScope.launch {
 88| registerForEventUseCase(eventId, userId).collect { resource ->
 89| when (resource) {
 90| is Resource.Loader -> {
 91| // Already handling loading state above
 92| }
 93| is Resource.Success -> {
 94| emitSideEffect(EventDetailsSideEffect.ShowToast("Registration successful!"))
 95| // Reload event details to get updated capacity and registration status
 96| loadEventDetails(eventId)
 97| }
 98| is Resource.Error -> {
 99| updateState {
100| if (it is EventDetailsState.Success) {
101| it.copy(isRegistering = false)
102| } else it
103| }
104| emitSideEffect(EventDetailsSideEffect.ShowToast(resource.errorMessage))
105| }
106| }
107| }
108| }
109| }
110|
111| private fun cancelRegistration() {
112| val eventId = currentEventId ?: return
113| val currentState = state.value
114| if (currentState !is EventDetailsState.Success) return
115|
116| val registrationId = currentState.registrationId ?: run {
117| emitSideEffect(EventDetailsSideEffect.ShowToast("No active registration found"))
118| return
119| }
120|
121| // Update UI to show cancelling state
122| updateState {
123| (it as EventDetailsState.Success).copy(isRegistering = true)
124| }
125|
126| viewModelScope.launch {
127| cancelRegistrationUseCase(registrationId).collect { resource ->
128| when (resource) {
129| is Resource.Loader -> {
130| // Already handling loading state above
131| }
132| is Resource.Success -> {
133| emitSideEffect(EventDetailsSideEffect.ShowToast("Registration cancelled"))
134| // Reload event details to get updated capacity
135| loadEventDetails(eventId)
136| }
137| is Resource.Error -> {
138| updateState {
139| if (it is EventDetailsState.Success) {
140| it.copy(isRegistering = false)
141| } else it
142| }
143| emitSideEffect(EventDetailsSideEffect.ShowToast(resource.errorMessage))
144| }
145| }
146| }
147| }
148| }
