152| registrationStatus == RegistrationStatus.CONFIRMED -> "Registered"
153| registrationStatus == RegistrationStatus.WAITLISTED -> "Waitlisted"
154| isFull -> "Join Waitlist"
155| else -> "Register Now"
156| }
157| }
