 23|class HomeAdapter(
 24| private val onEventClick: (Int) -> Unit,
 25| private val onCategoryClick: (Int) -> Unit,
 26| private val onViewAllClick: () -> Unit,
 27| private val onNotificationClick: () -> Unit,
 28| private val onProfileClick: () -> Unit
 29|) : ListAdapter<HomeItem, RecyclerView.ViewHolder>(HomeItemDiffCallback()) {
 30|
 31| companion object {
 32| private const val VIEW_TYPE_HEADER = 0
 33| private const val VIEW_TYPE_WELCOME = 1
 34| private const val VIEW_TYPE_UPCOMING_EVENTS = 2
 35| private const val VIEW_TYPE_CATEGORIES = 3
 36| private const val VIEW_TYPE_TRENDING = 4
 37| }
 38|
 39| override fun getItemViewType(position: Int): Int {
 40| return when (getItem(position)) {
 41| is HomeItem.Header -> VIEW_TYPE_HEADER
 42| is HomeItem.Welcome -> VIEW_TYPE_WELCOME
 43| is HomeItem.UpcomingEventsSection -> VIEW_TYPE_UPCOMING_EVENTS
 44| is HomeItem.CategoriesSection -> VIEW_TYPE_CATEGORIES
 45| is HomeItem.TrendingEventsSection -> VIEW_TYPE_TRENDING
 46| }
 47| }
 48|
 49| override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerView.ViewHolder {
 50| val inflater = LayoutInflater.from(parent.context)
 51| return when (viewType) {
 52| VIEW_TYPE_HEADER -> {
 53| val binding = ItemHomeHeaderBinding.inflate(inflater, parent, false)
 54| HeaderViewHolder(binding, onNotificationClick, onProfileClick)
 55| }
 56| VIEW_TYPE_WELCOME -> {
 57| val binding = ItemHomeWelcomeBinding.inflate(inflater, parent, false)
 58| WelcomeViewHolder(binding)
 59| }
 60| VIEW_TYPE_UPCOMING_EVENTS -> {
 61| val binding = ItemHomeUpcomingEventsBinding.inflate(inflater, parent, false)
 62| UpcomingEventsViewHolder(binding, onEventClick, onViewAllClick)
 63| }
 64| VIEW_TYPE_CATEGORIES -> {
 65| val binding = ItemHomeCategoriesBinding.inflate(inflater, parent, false)
 66| CategoriesViewHolder(binding, onCategoryClick)
 67| }
 68| VIEW_TYPE_TRENDING -> {
 69| val binding = ItemHomeTrendingBinding.inflate(inflater, parent, false)
 70| TrendingViewHolder(binding, onEventClick)
 71| }
 72| else -> throw IllegalArgumentException("Unknown view type: $viewType")
 73| }
 74| }
 75|
 76| override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {
 77| when (val item = getItem(position)) {
 78| is HomeItem.Header -> (holder as HeaderViewHolder).bind(item)
 79| is HomeItem.Welcome -> (holder as WelcomeViewHolder).bind(item)
 80| is HomeItem.UpcomingEventsSection -> (holder as UpcomingEventsViewHolder).bind(item)
 81| is HomeItem.CategoriesSection -> (holder as CategoriesViewHolder).bind(item)
 82| is HomeItem.TrendingEventsSection -> (holder as TrendingViewHolder).bind(item)
 83| }
 84| }
 85|
 86| // ViewHolder: Header
 87| class HeaderViewHolder(
 88| private val binding: ItemHomeHeaderBinding,
 89| private val onNotificationClick: () -> Unit,
 90| private val onProfileClick: () -> Unit
 91| ) : RecyclerView.ViewHolder(binding.root) {
 92|
 93| fun bind(item: HomeItem.Header) {
 94| with(binding) {
 95| // Show/hide notification badge
 96| if (item.hasNotifications) {
 97| vNotificationBadge.visible()
 98| } else {
 99| vNotificationBadge.gone()
100| }
101|
102| // Click listeners
103| flNotificationBell.setOnClickListener { onNotificationClick() }
104| ivUserAvatar.setOnClickListener { onProfileClick() }
105| }
106| }
107| }
108|
109| // ViewHolder: Welcome
110| class WelcomeViewHolder(
111| private val binding: ItemHomeWelcomeBinding
112| ) : RecyclerView.ViewHolder(binding.root) {
113|
114| fun bind(item: HomeItem.Welcome) {
115| binding.tvWelcomeTitle.text = binding.root.context.getString(
116| R.string.home_welcome_title,
117| item.userName
118| )
119| }
120| }
121|
122| // ViewHolder: Upcoming Events Section
123| class UpcomingEventsViewHolder(
124| private val binding: ItemHomeUpcomingEventsBinding,
125| private val onEventClick: (Int) -> Unit,
126| private val onViewAllClick: () -> Unit
127| ) : RecyclerView.ViewHolder(binding.root) {
128|
129| private val eventsAdapter = EventsAdapter(onEventClick)
130|
131| init {
132| binding.rvUpcomingEvents.apply {
133| adapter = eventsAdapter
134| layoutManager = LinearLayoutManager(context)
135| }
136|
137| binding.tvViewAll.setOnClickListener {
138| onViewAllClick()
139| }
140| }
141|
142| fun bind(item: HomeItem.UpcomingEventsSection) {
143| eventsAdapter.submitList(item.events)
144| }
145| }
146|
147| // ViewHolder: Categories Section
148| class CategoriesViewHolder(
149| private val binding: ItemHomeCategoriesBinding,
150| private val onCategoryClick: (Int) -> Unit
151| ) : RecyclerView.ViewHolder(binding.root) {
152|
153| private val categoriesAdapter = CategoriesAdapter(onCategoryClick)
154|
155| init {
156| binding.rvCategories.apply {
157| adapter = categoriesAdapter
158| layoutManager = GridLayoutManager(context, 3)
159| }
160| }
161|
162| fun bind(item: HomeItem.CategoriesSection) {
163| categoriesAdapter.submitList(item.categories)
164| }
165| }
166|
167| // ViewHolder: Trending Events Section
168| class TrendingViewHolder(
169| private val binding: ItemHomeTrendingBinding,
170| private val onEventClick: (Int) -> Unit
171| ) : RecyclerView.ViewHolder(binding.root) {
172|
173| private val trendingAdapter = TrendingEventsAdapter(onEventClick)
174|
175| init {
176| binding.rvTrendingEvents.apply {
177| adapter = trendingAdapter
178| layoutManager = LinearLayoutManager(context, LinearLayoutManager.HORIZONTAL, false)
179| }
180| }
181|
182| fun bind(item: HomeItem.TrendingEventsSection) {
183| trendingAdapter.submitList(item.events)
184| }
185| }
186|
187| // DiffCallback
188| private class HomeItemDiffCallback : DiffUtil.ItemCallback<HomeItem>() {
189| override fun areItemsTheSame(oldItem: HomeItem, newItem: HomeItem): Boolean {
190| return when {
191| oldItem is HomeItem.Header && newItem is HomeItem.Header -> true
192| oldItem is HomeItem.Welcome && newItem is HomeItem.Welcome -> true
193| oldItem is HomeItem.UpcomingEventsSection && newItem is HomeItem.UpcomingEventsSection -> true
194| oldItem is HomeItem.CategoriesSection && newItem is HomeItem.CategoriesSection -> true
195| oldItem is HomeItem.TrendingEventsSection && newItem is HomeItem.TrendingEventsSection -> true
196| else -> false
197| }
198| }
199|
200| override fun areContentsTheSame(oldItem: HomeItem, newItem: HomeItem): Boolean {
201| return oldItem == newItem
202| }
203| }
