142| state.events.map { event ->
143| CategoryEventsItem.EventCard(
144| event = event,
145| isRegistered = event.registrationStatus != null,
146| isWaitlisted = event.isWaitlisted
147| )
148| }
149| )
150|
151| // Show/hide empty state
152| if (state.events.isEmpty()) {
153| binding.llEmptyState.visible()
154| binding.rvCategoryEvents.gone()
155| } else {
156| binding.llEmptyState.gone()
157| binding.rvCategoryEvents.visible()
158| categoryEventsAdapter.submitList(items)
159| }
