211| private class CategoryEventsItemDiffCallback : DiffUtil.ItemCallback<CategoryEventsItem>() {
212| override fun areItemsTheSame(
213| oldItem: CategoryEventsItem,
214| newItem: CategoryEventsItem
215| ): Boolean {
216| return when {
