152| if (state.events.isEmpty()) {
153| binding.llEmptyState.visible()
154| binding.rvCategoryEvents.gone()
155| } else {
156| binding.llEmptyState.gone()
157| binding.rvCategoryEvents.visible()
158| categoryEventsAdapter.submitList(items)
159| }
