 44| private fun loadEvents(categoryId: Int) {
 45| currentCategoryId = categoryId
 46| updateState { CategoryEventsState.IsLoading(true) }
 47|
 48| viewModelScope.launch {
 49| try {
 50| // Get category info first
 51| val category = getCategoryById(categoryId)
 52| if (category == null) {
 53| updateState { CategoryEventsState.Error(CategoryEventsError.CategoryNotFound) }
 54| emitSideEffect(CategoryEventsSideEffect.ShowError(CategoryEventsError.CategoryNotFound))
