213| oldItem: CategoryEventsItem,
214| newItem: CategoryEventsItem
215| ): Boolean {
216| return when {
217| oldItem is CategoryEventsItem.Header && newItem is CategoryEventsItem.Header -> true
218| oldItem is CategoryEventsItem.EventCard && newItem is CategoryEventsItem.EventCard ->
219| oldItem.event.id == newItem.event.id
220| else -> false
221| }
222| }
