 19|@HiltViewModel
 20|class CategoryEventsViewModel @Inject constructor(
 21| private val getEventsByCategoryUseCase: GetEventsByCategoryUseCase,
 22| private val getCategoriesUseCase: GetCategoriesUseCase
 23|) : BaseViewModel<CategoryEventsState, CategoryEventsEvent, CategoryEventsSideEffect>(
 24| initialState = CategoryEventsState.Idle
 25|) {
 26|
 27| private var currentCategoryId: Int? = null
 28| private var currentSortBy: SortOption = SortOption.DATE
 29| private var currentFilterType: FilterType = FilterType.ALL_EVENTS
 30|
 31| override fun onEvent(event: CategoryEventsEvent) {
 32| when (event) {
 33| is CategoryEventsEvent.LoadEvents -> loadEvents(event.categoryId)
 34| is CategoryEventsEvent.OnSortChanged -> changeSorting(event.sortBy)
 35| is CategoryEventsEvent.OnFilterChanged -> changeFilter(event.filterType)
 36| is CategoryEventsEvent.OnFilterClicked -> emitSideEffect(CategoryEventsSideEffect.ShowFilterDialog)
 37| is CategoryEventsEvent.OnEventClicked -> emitSideEffect(CategoryEventsSideEffect.NavigateToEventDetails(event.eventId))
 38| is CategoryEventsEvent.OnBackClicked -> emitSideEffect(CategoryEventsSideEffect.NavigateBack)
 39| is CategoryEventsEvent.OnNotificationClicked -> emitSideEffect(CategoryEventsSideEffect.NavigateToNotifications)
 40| is CategoryEventsEvent.OnRetry -> currentCategoryId?.let { loadEvents(it) }
 41| }
 42| }
 43|
 44| private fun loadEvents(categoryId: Int) {
 45| currentCategoryId = categoryId
 46| updateState { CategoryEventsState.IsLoading(true) }
 47|
 48| viewModelScope.launch {
 49| try {
 50| // Get category info first
 51| val category = getCategoryById(categoryId)
 52| if (category == null) {
 53| updateState { CategoryEventsState.Error(CategoryEventsError.CategoryNotFound) }
 54| emitSideEffect(CategoryEventsSideEffect.ShowError(CategoryEventsError.CategoryNotFound))
 55| return@launch
 56| }
 57|
 58| // Load events for category
 59| getEventsByCategoryUseCase(categoryId, currentSortBy).collect { resource ->
 60| when (resource) {
 61| is Resource.Loader -> {
 62| updateState { CategoryEventsState.IsLoading(resource.isLoading) }
 63| }
 64| is Resource.Success -> {
 65| updateState {
 66| CategoryEventsState.Success(
 67| category = category,
 68| events = resource.data,
 69| sortBy = currentSortBy,
 70| hasActiveFilters = false,
 71| selectedFilter = currentFilterType,
 72| hasNotifications = false
 73| )
 74| }
 75| }
 76| is Resource.Error -> {
 77| val error = mapNetworkError(resource.error)
 78| updateState { CategoryEventsState.Error(error) }
 79| emitSideEffect(CategoryEventsSideEffect.ShowError(error))
 80| }
 81| }
 82| }
 83| } catch (e: Exception) {
 84| updateState { CategoryEventsState.Error(CategoryEventsError.UnknownError(e.message ?: "Unknown error")) }
 85| emitSideEffect(CategoryEventsSideEffect.ShowError(CategoryEventsError.UnknownError(e.message ?: "Unknown error")))
 86| }
 87| }
 88| }
 89|
 90| private suspend fun getCategoryById(categoryId: Int): Category? {
 91| return try {
 92| getCategoriesUseCase()
 93| .mapNotNull { resource ->
 94| if (resource is Resource.Success) {
 95| resource.data.find { it.id == categoryId }
 96| } else null
 97| }
 98| .firstOrNull()
 99| } catch (e: Exception) {
100| null
101| }
102| }
103|
104| private fun changeSorting(sortBy: SortOption) {
105| currentSortBy = sortBy
106| currentCategoryId?.let { loadEvents(it) }
107| }
108|
109| private fun changeFilter(filterType: FilterType) {
110| currentFilterType = filterType
111| // Update state with new filter
112| val currentState = state.value
113| if (currentState is CategoryEventsState.Success) {
114| updateState {
115| currentState.copy(selectedFilter = filterType)
116| }
117| }
118| // For now, filters don't actually filter data, just update UI state
119| // You can add actual filtering logic here if needed
120| }
121|
122| private fun mapNetworkError(error: NetworkError): CategoryEventsError {
123| return when (error) {
124| is NetworkError.Unauthorized -> CategoryEventsError.UnauthorizedError
125| is NetworkError.ServerError -> CategoryEventsError.ServerError
126| is NetworkError.NoInternet -> CategoryEventsError.NetworkError
127| else -> CategoryEventsError.UnknownError(error.toString())
128| }
129| }
