import android.os.Bundle
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.splashscreen.SplashScreen.Companion.installSplashScreen
import androidx.navigation.NavController
import androidx.navigation.NavDestination
import androidx.navigation.fragment.NavHostFragment
import androidx.navigation.ui.AppBarConfiguration
import androidx.navigation.ui.NavigationUI
import androidx.navigation.ui.setupActionBarWithNavController
import androidx.navigation.ui.setupWithNavController
import com.example.eventapplication.databinding.ActivityMainBinding
import com.google.android.material.snackbar.Snackbar
import dagger.hilt.android.AndroidEntryPoint

@AndroidEntryPoint
class MainActivity : AppCompatActivity(), NavController.OnDestinationChangedListener {

 private lateinit var binding: ActivityMainBinding
 private lateinit var navController: NavController

 override fun onCreate(savedInstanceState: Bundle?) {
 installSplashScreen()

 super.onCreate(savedInstanceState)
 binding = ActivityMainBinding.inflate(layoutInflater)
 setContentView(binding.root)

 setupNavigation()
 }

 private fun setupNavigation() {
 val navHostFragment = supportFragmentManager
 .findFragmentById(R.id.navHostFragment) as NavHostFragment
 navController = navHostFragment.navController

 // Define top-level destinations for the app bar
 val appBarConfiguration = AppBarConfiguration(
 setOf(R.id.homeFragment, R.id.browseFragment)
 )

 // Setup ActionBar with NavController
 setupActionBarWithNavController(navController, appBarConfiguration)

 // Connect the bottom navigation with NavController
 binding.bottomNavigation.setupWithNavController(navController)

 // Handle not-yet-implemented destinations
 binding.bottomNavigation.setOnItemReselectedListener { /* Prevent reload on reselection */ }
 binding.bottomNavigation.setOnItemSelectedListener { item ->
 when (item.itemId) {
 R.id.homeFragment, 
 R.id.browseFragment -> {
 NavigationUI.onNavDestinationSelected(item, navController)
 }
 R.id.navigation_my_events -> {
 Snackbar.make(binding.root, "My Events (Not implemented)", Snackbar.LENGTH_SHORT).show()
 false
 }
 R.id.navigation_updates -> {
 Snackbar.make(binding.root, "Notifications (Not implemented)", Snackbar.LENGTH_SHORT).show()
 false
 }
 else -> false
 }
 }

 // Listen for destination changes to control bottom nav visibility
 navController.addOnDestinationChangedListener(this)
 }

 override fun onDestinationChanged(
 controller: NavController,
 destination: NavDestination,
 arguments: Bundle?
 ) {
 // Control bottom navigation visibility based on destination
 when (destination.id) {
 // Hide on auth screens
 R.id.splashFragment,
 R.id.loginFragment,
 R.id.registerFragment -> {
 binding.bottomNavigation.visibility = View.GONE
 }

 // Show but unselect on detail screens
 R.id.categoryEventsFragment,
 R.id.eventDetailsFragment -> {
 binding.bottomNavigation.visibility = View.VISIBLE
 binding.bottomNavigation.menu.setGroupCheckable(0, false, true)
 }

 // Show and enable selection on main tabs
 else -> {
 binding.bottomNavigation.visibility = View.VISIBLE
 binding.bottomNavigation.menu.setGroupCheckable(0, true, true)
 }
 }
 }

 override fun onSupportNavigateUp(): Boolean {
 return navController.navigateUp() || super.onSupportNavigateUp()
 }
