import android.os.Bundle
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.splashscreen.SplashScreen.Companion.installSplashScreen
import androidx.navigation.NavController
import androidx.navigation.NavDestination
import androidx.navigation.findNavController
import androidx.navigation.ui.AppBarConfiguration
import androidx.navigation.ui.navigateUp
import androidx.navigation.ui.setupActionBarWithNavController
import androidx.navigation.ui.setupWithNavController
import com.example.eventapplication.databinding.ActivityMainBinding
import dagger.hilt.android.AndroidEntryPoint

@AndroidEntryPoint
class MainActivity : AppCompatActivity() {

 private lateinit var binding: ActivityMainBinding
 private lateinit var navController: NavController
 private lateinit var appBarConfiguration: AppBarConfiguration

 // Top-level destinations where bottom nav should be visible and selectable
 private val topLevelDestinations = setOf(
 R.id.homeFragment,
 R.id.browseFragment
 )

 // Auth screens where bottom nav should be hidden
 private val authScreens = setOf(
 R.id.splashFragment,
 R.id.loginFragment,
 R.id.registerFragment
 )

 // Detail screens where bottom nav should be visible but unselected
 private val detailScreens = setOf(
 R.id.categoryEventsFragment,
 R.id.eventDetailsFragment
 )

 override fun onCreate(savedInstanceState: Bundle?) {
 installSplashScreen()
 super.onCreate(savedInstanceState)

 binding = ActivityMainBinding.inflate(layoutInflater)
 setContentView(binding.root)

 setupNavigation()
 }

 private fun setupNavigation() {
 navController = findNavController(R.id.navHostFragment)

 // Configure app bar
 appBarConfiguration = AppBarConfiguration(topLevelDestinations)
 setupActionBarWithNavController(navController, appBarConfiguration)

 // Setup bottom navigation with automatic handling
 binding.bottomNavigation.setupWithNavController(navController)

 // Prevent unnecessary reloads when reselecting the same tab
 binding.bottomNavigation.setOnItemReselectedListener { /* Do nothing */ }

 // Control bottom navigation visibility based on destination
 navController.addOnDestinationChangedListener { _, destination, _ ->
 updateBottomNavVisibility(destination)
 }
 }

 private fun updateBottomNavVisibility(destination: NavDestination) = with(binding) {
 when (destination.id) {
 in authScreens -> {
 bottomNavigation.visibility = View.GONE
 }
 in detailScreens -> {
 bottomNavigation.visibility = View.VISIBLE
 bottomNavigation.menu.setGroupCheckable(0, false, true)
 }
 else -> {
 bottomNavigation.visibility = View.VISIBLE
 bottomNavigation.menu.setGroupCheckable(0, true, true)
 }
 }
 }


 override fun onSupportNavigateUp(): Boolean {
 return navController.navigateUp(appBarConfiguration) || super.onSupportNavigateUp()
 }
