EventDetailsAdapter.kt (Clean)
Kotlin

package com.example.eventapplication.presentation.eventdetails.adapter

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.eventapplication.databinding.*
import com.example.eventapplication.presentation.eventdetails.adapter.viewholder.*
import com.example.eventapplication.presentation.model.EventDetailsItem

class EventDetailsAdapter(
 private val listener: EventDetailsListener
) : ListAdapter<EventDetailsItem, RecyclerView.ViewHolder>(EventDetailsDiffCallback()) {

 private val sharedViewPool = RecyclerView.RecycledViewPool()

 override fun getItemViewType(position: Int): Int {
 return getItem(position).viewType
 }

 override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerView.ViewHolder {
 val inflater = LayoutInflater.from(parent.context)

 return when (viewType) {
 EventDetailsItem.VIEW_TYPE_IMAGE -> ImageViewHolder(
 ItemEventDetailsImageBinding.inflate(inflater, parent, false),
 listener
 )
 EventDetailsItem.VIEW_TYPE_INFO -> InfoViewHolder(
 ItemEventDetailsInfoBinding.inflate(inflater, parent, false)
 )
 EventDetailsItem.VIEW_TYPE_ACTION -> ActionViewHolder(
 ItemEventDetailsActionBinding.inflate(inflater, parent, false),
 listener
 )
 EventDetailsItem.VIEW_TYPE_DESCRIPTION -> DescriptionViewHolder(
 ItemEventDetailsDescriptionBinding.inflate(inflater, parent, false)
 )
 EventDetailsItem.VIEW_TYPE_AGENDA -> AgendaViewHolder(
 ItemEventDetailsAgendaBinding.inflate(inflater, parent, false),
 sharedViewPool
 )
 EventDetailsItem.VIEW_TYPE_SPEAKERS -> SpeakersViewHolder(
 ItemEventDetailsSpeakersBinding.inflate(inflater, parent, false),
 sharedViewPool
 )
 else -> throw IllegalArgumentException("Unknown view type: $viewType")
 }
 }

 override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {
 when (val item = getItem(position)) {
 is EventDetailsItem.Image -> (holder as ImageViewHolder).bind(item)
 is EventDetailsItem.Info -> (holder as InfoViewHolder).bind(item)
 is EventDetailsItem.Action -> (holder as ActionViewHolder).bind(item)
 is EventDetailsItem.Description -> (holder as DescriptionViewHolder).bind(item)
 is EventDetailsItem.AgendaSection -> (holder as AgendaViewHolder).bind(item)
 is EventDetailsItem.SpeakersSection -> (holder as SpeakersViewHolder).bind(item)
 }
 }
}
