Kotlin

sealed class EventDetailsItem {

 abstract val viewType: Int

 data class Image(...) : EventDetailsItem() {
 override val viewType = VIEW_TYPE_IMAGE
 }

 data class Info(...) : EventDetailsItem() {
 override val viewType = VIEW_TYPE_INFO
 }

 // ... other items

 companion object {
 const val VIEW_TYPE_IMAGE = 0
 const val VIEW_TYPE_INFO = 1
 const val VIEW_TYPE_ACTION = 2
 const val VIEW_TYPE_DESCRIPTION = 3
 const val VIEW_TYPE_AGENDA = 4
 const val VIEW_TYPE_SPEAKERS = 5
 }
}
