 override fun areItemsTheSame(oldItem: EventDetailsItem, newItem: EventDetailsItem): Boolean {
 return when {
 oldItem is EventDetailsItem.Image && newItem is EventDetailsItem.Image -> true
 oldItem is EventDetailsItem.Info && newItem is EventDetailsItem.Info -> true
 oldItem is EventDetailsItem.Action && newItem is EventDetailsItem.Action -> true
 oldItem is EventDetailsItem.Description && newItem is EventDetailsItem.Description -> true
 oldItem is EventDetailsItem.AgendaSection && newItem is EventDetailsItem.AgendaSection -> true
 oldItem is EventDetailsItem.SpeakersSection && newItem is EventDetailsItem.SpeakersSection -> true
 else -> false
 }
 }

 override fun areContentsTheSame(oldItem: EventDetailsItem, newItem: EventDetailsItem): Boolean {
 return oldItem == newItem
 }
 }
