import androidx.lifecycle.viewModelScope
import com.example.eventapplication.domain.usecase.auth.GetRememberedEmailUseCase
import com.example.eventapplication.domain.usecase.auth.LoginUseCase
import com.example.eventapplication.domain.usecase.auth.SaveRememberMeUseCase
import com.example.eventapplication.domain.usecase.validation.ValidateEmailUseCase
import com.example.eventapplication.domain.usecase.validation.ValidatePasswordUseCase
import com.example.eventapplication.domain.common.Resource
import com.example.eventapplication.domain.model.NetworkError
import com.example.eventapplication.domain.validation.EmailValidationError
import com.example.eventapplication.domain.validation.PasswordValidationError
import com.example.eventapplication.domain.validation.ValidationResult
import com.example.eventapplication.presentation.common.BaseViewModel
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class LoginViewModel @Inject constructor(
 private val loginUseCase: LoginUseCase,
 private val validateEmailUseCase: ValidateEmailUseCase,
 private val validatePasswordUseCase: ValidatePasswordUseCase,
 private val saveRememberMeUseCase: SaveRememberMeUseCase,
 private val getRememberedEmailUseCase: GetRememberedEmailUseCase
) : BaseViewModel<LoginState, LoginEvent, LoginSideEffect>(LoginState()) {

 private var email = ""
 private var password = ""
 private var rememberMe = false

 init {
 loadRememberedCredentials()
 }

 private fun loadRememberedCredentials() {
 viewModelScope.launch {
 val (isRemembered, rememberedEmail) = getRememberedEmailUseCase()
 if (isRemembered) {
 email = rememberedEmail
 rememberMe = true
 updateState { it.copy(rememberMe = true, email = rememberedEmail) }
 }
 }
 }

 override fun onEvent(event: LoginEvent) {
 when (event) {
 is LoginEvent.EmailChanged -> {
 email = event.email
 }
 is LoginEvent.PasswordChanged -> {
 password = event.password
 }
 is LoginEvent.RememberMeChanged -> {
 rememberMe = event.rememberMe
 updateState { it.copy(rememberMe = event.rememberMe) }
 }
 is LoginEvent.LoginClicked -> {
 handleLogin()
 }
 is LoginEvent.NavigateToRegisterClicked -> {
 emitSideEffect(LoginSideEffect.NavigateToRegister)
 }
 is LoginEvent.ForgotPasswordClicked -> {
 emitSideEffect(LoginSideEffect.ShowError("Forgot password feature coming soon"))
 }
 }
 }

 private fun handleLogin() {
 // Validate inputs
 val emailValidation = validateEmailUseCase(email)
 if (emailValidation is ValidationResult.Error) {
 emitSideEffect(LoginSideEffect.ShowError(emailValidation.error.toErrorMessage()))
 return
 }

 val passwordValidation = validatePasswordUseCase(password)
 if (passwordValidation is ValidationResult.Error) {
 emitSideEffect(LoginSideEffect.ShowError(passwordValidation.error.toErrorMessage()))
 return
 }

 // Perform login
 viewModelScope.launch {
 loginUseCase(email, password, rememberMe).collect { resource ->
 when (resource) {
 is Resource.Loader -> {
 updateState { it.copy(isLoading = resource.isLoading) }
 }
 is Resource.Success -> {
 updateState { it.copy(isLoading = false) }
 // Save token and user data only if remember me is checked
 if (rememberMe) {
 loginUseCase.saveToken(resource.data.token)
 loginUseCase.saveUserData(resource.data)
 saveRememberMeUseCase(true, email)
 } else {
 saveRememberMeUseCase(false, "")
 }
 emitSideEffect(LoginSideEffect.NavigateToHome)
 }
 is Resource.Error -> {
 updateState { it.copy(isLoading = false) }
 val errorMessage = when (resource.error) {
 is NetworkError.Unknown -> resource.error.message ?: "Login failed"
 is NetworkError.Unauthorized -> "Invalid credentials"
 else -> "Login failed"
 }
 emitSideEffect(LoginSideEffect.ShowError(errorMessage))
 }
 }
 }
 }
 }

 // Helper functions to map typed errors to strings
 // TODO: Refactor to use Context-based string resources
 private fun EmailValidationError.toErrorMessage(): String {
 return when (this) {
 EmailValidationError.Empty -> "Email cannot be empty"
 EmailValidationError.InvalidFormat -> "Invalid email format"
 }
 }

 private fun PasswordValidationError.toErrorMessage(): String {
 return when (this) {
 PasswordValidationError.Empty -> "Password cannot be empty"
 PasswordValidationError.TooShort -> "Password must be at least 8 characters"
 PasswordValidationError.NoUppercase -> "Password must contain at least one uppercase letter"
 PasswordValidationError.NoLowercase -> "Password must contain at least one lowercase letter"
 PasswordValidationError.NoDigit -> "Password must contain at least one number"
 }
 }
}
