 46|
 47| override fun onEvent(event: LoginEvent) {
 48| when (event) {
 49| is LoginEvent.EmailChanged -> {
 50| email = event.email
 51| }
 52| is LoginEvent.PasswordChanged -> {
 53| password = event.password
 54| }
 55| is LoginEvent.RememberMeChanged -> {
 56| rememberMe = event.rememberMe
 57| updateState { it.copy(rememberMe = event.rememberMe) }
 58| }
 59| is LoginEvent.LoginClicked -> {
 60| handleLogin()
 61| }
 62| is LoginEvent.NavigateToRegisterClicked -> {
 63| emitSideEffect(LoginSideEffect.NavigateToRegister)
 64| }
 65| is LoginEvent.ForgotPasswordClicked -> {
 66| emitSideEffect(LoginSideEffect.ShowError("Forgot password feature coming soon"))
 67| }
 68| }
 69| }
 70|
 71| private fun handleLogin() {
 72| // Validate inputs
 73| val emailValidation = validateEmailUseCase(email)
 74| if (emailValidation is ValidationResult.Error) {
 75| emitSideEffect(LoginSideEffect.ShowError(emailValidation.error.toErrorMessage()))
 76| return
 77| }
 78|
 79| val passwordValidation = validatePasswordUseCase(password)
 80| if (passwordValidation is ValidationResult.Error) {
 81| emitSideEffect(LoginSideEffect.ShowError(passwordValidation.error.toErrorMessage()))
 82| return
 83| }
 84|
 85| // Perform login
 86| viewModelScope.launch {
 87| loginUseCase(email, password, rememberMe).collect { resource ->
 88| when (resource) {
 89| is Resource.Loader -> {
 90| updateState { it.copy(isLoading = resource.isLoading) }
 91| }
 92| is Resource.Success -> {
 93| updateState { it.copy(isLoading = false) }
 94| // Save token and user data only if remember me is checked
 95| if (rememberMe) {
 96| loginUseCase.saveToken(resource.data.token)
 97| loginUseCase.saveUserData(resource.data)
 98| saveRememberMeUseCase(true, email)
 99| } else {
100| saveRememberMeUseCase(false, "")
101| }
102| emitSideEffect(LoginSideEffect.NavigateToHome)
103| }
104| is Resource.Error -> {
105| updateState { it.copy(isLoading = false) }
106| val errorMessage = when (resource.error) {
107| is NetworkError.Unknown -> resource.error.message ?: "Login failed"
108| is NetworkError.Unauthorized -> "Invalid credentials"
109| else -> "Login failed"
110| }
111| emitSideEffect(LoginSideEffect.ShowError(errorMessage))
112| }
113| }
114| }
115| }
116| }
