import androidx.lifecycle.viewModelScope
import com.example.eventapplication.domain.usecase.auth.RegisterUseCase
import com.example.eventapplication.domain.usecase.department.GetDepartmentsUseCase
import com.example.eventapplication.domain.usecase.validation.ValidateEmailUseCase
import com.example.eventapplication.domain.usecase.validation.ValidateNameUseCase
import com.example.eventapplication.domain.usecase.validation.ValidatePasswordUseCase
import com.example.eventapplication.domain.usecase.validation.ValidatePhoneUseCase
import com.example.eventapplication.domain.common.Resource
import com.example.eventapplication.domain.model.NetworkError
import com.example.eventapplication.domain.validation.EmailValidationError
import com.example.eventapplication.domain.validation.NameValidationError
import com.example.eventapplication.domain.validation.PasswordValidationError
import com.example.eventapplication.domain.validation.PhoneValidationError
import com.example.eventapplication.domain.validation.ValidationResult
import com.example.eventapplication.presentation.common.BaseViewModel
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class RegisterViewModel @Inject constructor(
 private val registerUseCase: RegisterUseCase,
 private val getDepartmentsUseCase: GetDepartmentsUseCase,
 private val validateEmailUseCase: ValidateEmailUseCase,
 private val validatePasswordUseCase: ValidatePasswordUseCase,
 private val validateNameUseCase: ValidateNameUseCase,
 private val validatePhoneUseCase: ValidatePhoneUseCase
) : BaseViewModel<RegisterState, RegisterEvent, RegisterSideEffect>(RegisterState()) {

 private var firstName = ""
 private var lastName = ""
 private var email = ""
 private var phone = ""
 private var department = ""
 private var selectedDepartmentId: Int? = null
 private var password = ""
 private var confirmPassword = ""
 private var otp = ""
 private var termsAccepted = false

 init {
 loadDepartments()
 }

 private fun loadDepartments() {
 viewModelScope.launch {
 getDepartmentsUseCase().collect { resource ->
 when (resource) {
 is Resource.Success -> {
 updateState { it.copy(departments = resource.data) }
 }
 is Resource.Error -> {
 val errorMessage = when (resource.error) {
 is NetworkError.Unknown -> resource.error.message ?: "Failed to load departments"
 else -> "Failed to load departments"
 }
 emitSideEffect(RegisterSideEffect.ShowError(errorMessage))
 }
 is Resource.Loader -> {
 // Optional: show loading state for departments
 }
 }
 }
 }
 }

 override fun onEvent(event: RegisterEvent) {
 when (event) {
 is RegisterEvent.FirstNameChanged -> {
 firstName = event.firstName
 }
 is RegisterEvent.LastNameChanged -> {
 lastName = event.lastName
 }
 is RegisterEvent.EmailChanged -> {
 email = event.email
 }
 is RegisterEvent.PhoneChanged -> {
 phone = event.phone
 }
 is RegisterEvent.DepartmentSelected -> {
 department = event.department
 val dept = state.value.departments.find { it.name == event.department }
 selectedDepartmentId = dept?.id
 updateState { it.copy(selectedDepartment = event.department) }
 }
 is RegisterEvent.PasswordChanged -> {
 password = event.password
 }
 is RegisterEvent.ConfirmPasswordChanged -> {
 confirmPassword = event.confirmPassword
 }
 is RegisterEvent.TermsAcceptedChanged -> {
 termsAccepted = event.accepted
 updateState { it.copy(termsAccepted = event.accepted) }
 }
 is RegisterEvent.SendOtpClicked -> {
 emitSideEffect(RegisterSideEffect.ShowMessage("OTP feature is currently disabled"))
 }
 is RegisterEvent.OtpChanged -> {
 otp = event.otp
 }
 is RegisterEvent.RegisterClicked -> {
 handleRegister()
 }
 is RegisterEvent.NavigateToLoginClicked -> {
 emitSideEffect(RegisterSideEffect.NavigateToLogin)
 }
 }
 }

 private fun handleRegister() {
 // Validate first name
 val firstNameValidation = validateNameUseCase(firstName)
 if (firstNameValidation is ValidationResult.Error) {
 emitSideEffect(RegisterSideEffect.ShowError(firstNameValidation.error.toErrorMessage("First name")))
 return
 }

 // Validate last name
 val lastNameValidation = validateNameUseCase(lastName)
 if (lastNameValidation is ValidationResult.Error) {
 emitSideEffect(RegisterSideEffect.ShowError(lastNameValidation.error.toErrorMessage("Last name")))
 return
 }

 // Validate email
 val emailValidation = validateEmailUseCase(email)
 if (emailValidation is ValidationResult.Error) {
 emitSideEffect(RegisterSideEffect.ShowError(emailValidation.error.toErrorMessage()))
 return
 }

 // Validate phone
 val phoneValidation = validatePhoneUseCase(phone)
 if (phoneValidation is ValidationResult.Error) {
 emitSideEffect(RegisterSideEffect.ShowError(phoneValidation.error.toErrorMessage()))
 return
 }

 // Validate department
 if (selectedDepartmentId == null) {
 emitSideEffect(RegisterSideEffect.ShowError("Please select a department"))
 return
 }

 // Validate password
 val passwordValidation = validatePasswordUseCase(password)
 if (passwordValidation is ValidationResult.Error) {
 emitSideEffect(RegisterSideEffect.ShowError(passwordValidation.error.toErrorMessage()))
 return
 }

 // Validate password confirmation
 if (password != confirmPassword) {
 emitSideEffect(RegisterSideEffect.ShowError("Passwords do not match"))
 return
 }

 // Validate terms acceptance
 if (!termsAccepted) {
 emitSideEffect(RegisterSideEffect.ShowError("You must accept the terms of service"))
 return
 }

 // Perform registration
 viewModelScope.launch {
 registerUseCase(
 email = email,
 password = password,
 firstName = firstName,
 lastName = lastName,
 mobileNumber = phone,
 departmentId = selectedDepartmentId!!,
 confirmPassword = confirmPassword
 ).collect { resource ->
 when (resource) {
 is Resource.Loader -> {
 updateState { it.copy(isLoading = resource.isLoading) }
 }
 is Resource.Success -> {
 updateState { it.copy(isLoading = false) }
 emitSideEffect(RegisterSideEffect.ShowMessage(resource.data.message))
 emitSideEffect(RegisterSideEffect.NavigateToLogin)
 }
 is Resource.Error -> {
 updateState { it.copy(isLoading = false) }
 val errorMessage = when (resource.error) {
 is NetworkError.Unknown -> resource.error.message ?: "Registration failed"
 else -> "Registration failed"
 }
 emitSideEffect(RegisterSideEffect.ShowError(errorMessage))
 }
 }
 }
 }
 }

 // Helper functions to map typed errors to strings
 // TODO: Refactor to use Context-based string resources
 private fun EmailValidationError.toErrorMessage(): String {
 return when (this) {
 EmailValidationError.Empty -> "Email cannot be empty"
 EmailValidationError.InvalidFormat -> "Invalid email format"
 }
 }

 private fun PasswordValidationError.toErrorMessage(): String {
 return when (this) {
 PasswordValidationError.Empty -> "Password cannot be empty"
 PasswordValidationError.TooShort -> "Password must be at least 8 characters"
 PasswordValidationError.NoUppercase -> "Password must contain at least one uppercase letter"
 PasswordValidationError.NoLowercase -> "Password must contain at least one lowercase letter"
 PasswordValidationError.NoDigit -> "Password must contain at least one number"
 }
 }

 private fun NameValidationError.toErrorMessage(fieldName: String): String {
 return when (this) {
 NameValidationError.Empty -> "$fieldName cannot be empty"
 NameValidationError.TooShort -> "$fieldName must be at least 2 characters"
 }
 }

 private fun PhoneValidationError.toErrorMessage(): String {
 return when (this) {
 PhoneValidationError.Empty -> "Phone number cannot be empty"
 PhoneValidationError.InvalidFormat -> "Please enter a valid phone number"
 }
 }
